    function addToken(){
        setToken(token)
        localStorage.setItem('userToken1',token)
    }

    return(
        <AuthContext.Provider value={{user,setUser, token, setToken}}>
            {children}
        </AuthContext.Provider>
    )
}

export default AuthProvider;
