import styled from "styled-components"
import { Link, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

export default function TelaCadastro({ icon }) {

    const [ form, setForm] = useState({email: "", name: "", image: "", password:""})

    const navigate = useNavigate("/")

    function Cadastrar(){
        const URL = 'https://mock-api.driven.com.br/api/v4/driven-plus/auth/sign-up'
    }
    function Formulario(e){
        const {name, value} = e.target
        setForm({...form, [name]: value })
        console.log(form)
    }

    return (
        <>
            <Icon>
                <img src={icon} alt="icon" />
            </Icon>
            <InputLogin>
                <input
                    name="name"
                    type="text"
                    placeholder="Nome"
                    value={form.email} 
                        onChange={Formulario} 
                />
                <input
                    name="cpf"
                    type="text"
                    placeholder="CPF"
                />
                <input
                    name="email"
                    type="text"
                    placeholder="E-mail"
                />
                <input
                    name="password"
                    type="text"
                    placeholder="Senha"
                />
            </InputLogin>

            <DivButton>
                <button> CADASTRAR </button>
            </DivButton>

            <Link to="/">
                <Text>
                    <p>Já possuí uma conta? Entre</p>
                </Text>
            </Link>
        </>

    )
}

const Icon = styled.div`
    display: flex;
    justify-content: center;
    padding: 134px 38px 0 38px;
    img{
        width: 299px;
        height: 49px;
    }
`
const InputLogin = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-top: 100px;
    input{
        border-radius: 8px;
        width: 299px;
        height: 52px;
        background-color: #FFFFFF;
        margin-bottom: 16px;
        padding: 5%;
        cursor: text;
}
`
const DivButton = styled.div`
    width: auto;
    display: flex;
    justify-content: center;
    align-items: center;
    button{
        width: 299px;
        height: 52px;
        color: #FFFFFF;
        border: none;
        background-color: #FF4791;
        border-radius: 8px;
        font-weight: 700;
        cursor: pointer;
    }
`
const Text = styled.div`
    margin-top: 24px;
    width: auto;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #FFFFFF;
    cursor: pointer;
`
