import styled from "styled-components"
export default function InfoPLano({ calendar, money, info, setInfo }) {
    console.log(info)
    return (
        <>
            <Info>
                <Titulo>
                    <img src={calendar} alt="calendário" />
                    <p>Benefício:</p>
                </Titulo>
                <ComponentBox>
                {info.map((a) =>
                        <p>{a.perks.id}. {a.perks.title}</p>
                        )}
                    <p>1. Brindes exclusivos</p>
                    <p>2. Materiais bônus de web</p>
                </ComponentBox>
                <Titulo>
                    <img src={money} alt="" />
                    <p>Preço:</p>
                </Titulo>
                <ComponentBox>
                    <p>R$ 39,99 cobrados mensalmente</p>
                </ComponentBox>
            </Info>
        </>
    )
}
const Info = styled.div`
    display: flex;
    flex-direction: column;
    margin: 0 auto;
    width: 299px;
    height: auto;
    margin-bottom: 34px;
`
const Titulo = styled.div`
    margin-bottom: 10px;
    height: auto;
    display: flex;
    justify-content: start;
    align-items: center;
    font-weight: 400;
    font-size: 16px;
    line-height: 19px;
    color:#FFFFFF;
    img{
        width: 12px;
        height: 16px;
        margin-right: 5px;
    }
`
const ComponentBox = styled.div`
    margin-bottom: 12px;
    display: flex;
    flex-direction: column;
    color:#FFFFFF;
    font-size: 14px;
    line-height: 16px;
`
