import styled from "styled-components"

export default function Form({abrirModal}){
    const [form, setForm] = useState({ membershipId: "", cardName: "", cardNumber: "", securityNumber: "" })
    /*membershipId: 1,
    cardName: "Fulano Da Silva",
    cardNumber: "1234 1234 1234 1234",
    securityNumber: 123,
    expirationDate: "01/28" */
    return(
        <>
        <InfoCartao>
        <input
            type="text"
            placeholder="Nome impresso no cartão"
        />
        <input
            type="text"
            placeholder="Digitos do cartão"
        />
        <InputMenor>
            <input
                type="text"
                placeholder="Código de segurança"
            />
            <input
                type="text"
                placeholder="Validade"
            />
        </InputMenor>
    </InfoCartao>

    <Assinar>
        <button onClick={abrirModal}>
            ASSINAR
        </button>
    </Assinar>
    </>
    )
}

const InfoCartao = styled.div`
    display: flex;
    flex-direction: column;
    margin: 0 auto;
    width: 299px;
    height: auto;
    input{
        border-radius: 8px;
        width: 299px;
        height: 52px;
        background-color: #FFFFFF;
        margin-bottom: 16px;
        padding: 5%;
        cursor: text;
        color: #7E7E7E;
        font-weight: 400;
        font-size: 14px;
        line-height: 16px;
    }
`
const InputMenor = styled.div`
    display: flex;
    justify-content: space-between;
    input{
        width: 49%;
    }
`
const Assinar = styled.div`
    button{
        width: 299px;
        height: 52px;
        color: #FFFFFF;
        border: none;
        background-color: #FF4791;
        border-radius: 8px;
        font-weight: 700;
        cursor: pointer;
    }
`
