import styled from "styled-components"
import { Link, useNavigate } from "react-router-dom";
import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { AuthContext } from "../contexts/AuthContext";

export default function TelaLogin({ icon }) {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const { user, setUser } = useContext(AuthContext)

    function Logar() {
        const navigate = useNavigate()

        const obj = {
            password: password,
            email: email
        }

        const URL = 'https://mock-api.driven.com.br/api/v4/driven-plus/auth/login'
        const post = axios.post(URL, obj)

        post.then((ress) => {
            console.log(ress.data)
            setUser(ress.data)
            navigate('/subscriptions')

        })

        post.catch((err) => {
            if (err.response.status == 422) {
                alert("Usuário não existente. Por favor, faça o cadastro.")
            } else {
                alert(err.response.data.message)
            }
            window.location.reload();
        })
    }
    return (
        <>
            <Icon>
                <img src={icon} alt="icon" />
            </Icon>
            <InputLogin>
                <input
                    type="text"
                    placeholder="E-mail"
                />
                <input
                    type="text"
                    placeholder="Senha"
                />
            </InputLogin>
            <Link to="/subscriptions">
            <DivButton>
                <button onClick={Loga}> ENTRAR </button>
            </DivButton>
            </Link>

            <Link to="/sign-up">
                <Text>
                    <p>Não possuí uma conta? Cadastre-se</p>
                </Text>
            </Link>
        </>
    )
}

const Icon = styled.div`
    display: flex;
    justify-content: center;
    padding: 134px 38px 0 38px;
    img{
        width: 299px;
        height: 49px;
    }
`
const InputLogin = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-top: 100px;
    input{
        border-radius: 8px;
        width: 299px;
        height: 52px;
        background-color: #FFFFFF;
        margin-bottom: 16px;
        padding: 5%;
        cursor: text;
        color: #7E7E7E;
        font-weight: 400;
        font-size: 14px;
        line-height: 16px;
}

`
const DivButton = styled.div`
    width: auto;
    display: flex;
    justify-content: center;
    align-items: center;
    button{
        width: 299px;
        height: 52px;
        color: #FFFFFF;
        border: none;
        background-color: #FF4791;
        border-radius: 8px;
        font-weight: 700;
        cursor: pointer;
    }
`
const Text = styled.div`
    cursor: pointer;
    margin-top: 24px;
    width: auto;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #FFFFFF;
`
