import styled from "styled-components"
import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import Modal from 'react-modal';
import "./modal.css"

export default function CompModal({modalIsOpen, setIsOpen}){
    Modal.setAppElement("body");

    function fecharModal() {
        setIsOpen(false)
    }
    return(
        <>

        <Modal
                isOpen={modalIsOpen}
                onRequestClose={fecharModal}
                overlayClassName="modal-overlay"
                className="modal"
            >
                 <Close>x</Close>
                <p>Tem certeza que deseja assinar o plano Driven Plus (R$ 39,99)?</p>
                <Escolha onClick={fecharModal}>SIM</Escolha>
                <Escolha onClick={fecharModal}>N√ÉO</Escolha>

            </Modal>
        </>
    )
}
const Close = styled.button`
    width: 28px;
    height: 24px;
    background-color: #FFFFFF;
    position: fixed;
    top: 0;
    bottom: 0;
    left: 1;
    right: 0;
    margin: 50px;
`
const Escolha = styled.button`
    margin: 5%;
    width: 95px;
    height: 52px;
    color: #FFFFFF;
    border: none;
    background-color: #FF4791;
    border-radius: 8px;
    font-weight: 700;
    cursor: pointer;
`
